package com.cg.dayone;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			    int a = 12, b = 5;		   
			    System.out.println("a + b = " + (a + b));			    
			    System.out.println("a - b = " + (a - b));			    
			    System.out.println("a * b = " + (a * b));			   
			    System.out.println("a / b = " + (a / b));			   
			    System.out.println("a % b = " + (a % b));
			  }
			}